import React, { useState } from 'react';
import { TextField, Container, Typography, MenuItem, Grid, Button } from '@material-ui/core';
import { Form } from 'react-bootstrap';

import banner from '../assets/images/banner.jpg';
import extra from '../assets/images/extra.jpg'

function Home() {
  const [restaurant, setRestaurant] = useState('');
  const [menuchoice, setMenuchoice] = useState('');
  const [Name, setName] = useState('');
  const [MbNumber, setMbNumber] = useState('');
  const [address, setaddress] = useState('');

  const handleSubmit = event => {
    event.preventDefault();
    // check if mobile number is valid
    if (MbNumber.length !== 10) {
      alert("Please enter a valid mobile number");
      return;
    }
    // check if restaurant is selected
    if (document.getElementsByName('restaurant')[0].value === "") {
      alert("Please select a restaurant");
      return;
    }
    // check if menu choice is selected
    if (document.getElementsByName('menuchoice')[0].value === "") {
      alert("Please select a menu choice");
      return;
    }
    // navigate to order page
    window.location.href = "/payment";
    // reset form
    setName('');
    setMbNumber('');
    setaddress('');
  }

  return (
    <Container>
      <Typography variant="h4" align="center">Place Your Order</Typography>
      <Form onSubmit={handleSubmit}>
        <Grid container spacing={1}>
          <Grid item xs={12}>
            <TextField
              id="restaurant"
              label="Choose Restaurant"
              variant="outlined"
              value={restaurant}
              onChange={event => setRestaurant(event.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <Dropdown
              id="menuchoice"
              label="Menu Choice"
              variant="outlined"
              value={menuchoice}
              onChange={event => setMenuchoice(event.target.value)}
              required
            >
              <MenuItem value="">--Menu Choice--</MenuItem>
              <MenuItem value="combo1">Combo1</MenuItem>
              <MenuItem value="combo2">Combo2</MenuItem>
              <MenuItem value="combo3">Combo3</MenuItem>
              <MenuItem value="combo4">Combo4</MenuItem>
            </Dropdown>
          </Grid>
          <Grid item xs={12}>
            <TextField
              id="name"
              label="Name"
              variant="outlined"
              value={Name}
              onChange={event => setName(event.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              id="mbnumber"
              label="Mobile Number"
              variant="outlined"
              value={MbNumber}
              onChange={event => setMbNumber(event.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              id="address"
              label="Address"
              variant="outlined"
              value={address}
              onChange={event => setaddress(event.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <Button variant="contained" color="primary" type="submit">
              Submit
            </Button>
          </Grid>
        </Grid>
      </Form>
    </Container>
  );
}

export default Home;
